import React from 'react'
import {connect} from 'react-redux'
import {Link, withRouter} from 'react-router'
import {compose} from 'redux'
import R from 'ramda'
import classNames from 'classnames'

import {
  getCounts,
  getActiveCountId
} from 'selectors'

const Counts = ({counts, activeCountId}) => {
  console.log('activeCountId', activeCountId)
  const renderCount = (count, index) => {
    const getActiveState = R.propEq('id', activeCountId)

    const linkClass = classNames({
      'list-group-item': true,
      'active': getActiveState(count)
    })

    return (
      <Link
        to={`/counts/${count.id}`}
        className={linkClass}
        key={index}
      >
        {count.name}
      </Link>
    )
  }

  const renderAllCount = () => {
    const linkClass = classNames({
      'list-group-item': true,
      'active': R.isNil(activeCountId)
    })

    return (
      <Link
        to='/'
        className={linkClass}
      >
        All
      </Link>
    )
  }

  return (
    <div className='well'>
      <h4>Available items</h4>
      <div className='list-group'>
        {renderAllCount()}
        {counts.map((count, index) => renderCount(count, index))}
      </div>
    </div>
  )
}

const mapStateToProps = (state, ownProps) => ({
  counts: getCounts(state),
  activeCountId: getActiveCountId(ownProps)
})

export default compose(
  withRouter,
  connect(mapStateToProps, null)
)(Counts)
